import {SET_TASKS, SET_USERS} from '../../constants';
import {setUsers, setTasks} from '../actions/actionCreator';

export const usersReducer = (state = [], {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return users;
    default:
      return state;
  }
}

export const tasksReducer = (state = [], { type, task, tasks }) => {
  switch (type) {
    case SET_TASKS:
      return tasks
    default:
      return state;
  }
}

export const loadUsers = () => async (dispatch) => {
  const users = await fetch("/api/users").then(res => res.json());
  dispatch(setUsers(users))
}

export const loadTasks = () => async (dispatch) => {
  const tasks = await fetch("/api/tasks").then(res => res.json())
  dispatch(setTasks(tasks))
}

export const saveTask = (task) => async () => {
  await fetch("/api/tasks", {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify(task)
  })
}

export const removeTask = (id) => async () => {
  await fetch("/api/tasks/", {
    method: "DELETE",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify(id)
  })
}
