import jss from 'jss';
import preset from 'jss-preset-default';

jss.setup(preset())

const styles = {
  bodyForm: {
    margin: {
      bottom: '35px'
    },
    padding: '7px',
    border: '1px solid #f2f2f2',
    display: 'block',
    'box-shadow': '0 2px 5px rgba(122,122,122,0.2)',
    position: 'relative',
  },
  textareaStyle: {
    'vertical-align': 'top',
    padding: '2px 5px',
    'font-size': '12px',
    width: '100%',
    margin: '0 auto 5px',
    outline: 'none',
	  border: '1px solid #f2f2f2',
    'box-sizing': 'border-box',
    resize: 'none',
  },
  textareaDescription: {
    border: 'none',
    height: '100px'
  },
  textareaPriority: {
    height: '25px'
  },
  selectStyle: {
    'font-size': '12px',
    outline: 'none',
    width: '100%',
    height: '25px',
    display: 'block',
    margin: '0 auto 5px',
    border: '1px solid #d9d9d9',
    'box-sizing': 'border-box',
  },
  hintError: {
    'box-shadow': 'inset 0 0 6px rgba(255,0,0,1)'
  },
  hintValid: {
    'box-shadow': 'inset 0 0 6px rgba(0,255,0,1)'
  },
  hint: {
    display: 'none',
    'text-align': 'center',
    color: '#f13a13',
    'margin-top': '-25px'
  },
  popupHint: {
    display: 'block'
  },
}

const {classes} = jss.createStyleSheet(styles).attach()

export default classes;
