import {SET_TASKS, SET_USERS} from '../../constants';

export const setTasks = (tasks) => ({
  type: SET_TASKS,
  tasks
});

export const setUsers = (users) => ({
  type: SET_USERS,
  users
});

export const addTask = (task) => ({
  type: 'ADD_TASK',
  task
})
