import {SET_TASKS, SET_USERS, ADD_TASK} from '../../constants';

export const addTask = (task) => ({
  type: ADD_TASK,
  task
});

export const setTasks = (tasks) => ({
  type: SET_TASKS,
  tasks
});

export const setUsers = (users) => ({
  type: SET_USERS,
  users
});
