import React, { Component } from 'react';

import TodoBody from './containers/todoBody';

class App extends Component {

  state = {
    tasks: []
  }
  
  componentDidMount() {
    fetch('/api/tasks')
      .then(res => res.json())
      .then(tasks => this.setState({tasks}, () => console.log('tasks fetched..', tasks)));
  }
  render() {

    const {tasks} = this.state;

    return (
        <div className='App'>
          <h2>Hi</h2>
          <p>{tasks}</p>
        </div>
    );
  }
}

export default App;
