import {SET_TASKS, SET_USERS, ADD_TASK} from '../../constants';
import {setUsers, setTasks} from '../actions/actionCreator';

export const usersReducer = (state = [], {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return users;
    default:
      return state;
  }
}

export const loadUsers = () => async (dispatch) => {
  const users = await fetch("/api/users").then(res => res.json());
  dispatch(setUsers(users))
}

export const tasksReducer = (state = [], { type, tasks}) => {
  switch (type) {
    case ADD_TASK :
      return {...state, tasks: [tasks]}
    case SET_TASKS :
      return tasks
    default :
      return state;
  }
}

export const saveTasks = () => async (dispatch, getState) => {
  const tasks = getState().tasks;
  await fetch("/api/tasks", {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify(tasks)
  })
  alert("Success")
}

export const loadTasks = () => async (dispatch) => {
  const tasks = await fetch("/api/tasks").then(res => res.json())
  dispatch(setTasks(tasks))
}
