import React, { Component } from 'react';

import { InputBtn } from '../todoBtns/todoBtns';

import classes from './todoInput_style';

class TodoInput extends Component {
   state = {
     task: {
      id: '',
      description: '',
      executor: '',
      priority: '',
     },
     description: '',
     executor: '',
     priority: '',
     descrError: 'initial',
     execError: 'initial',
     priorError: 'initial',
   };

   handleChange = (e) => {
      this.setState({
        [e.target.name]: e.target.value
      })
   };

   validateDescription = () => {
     const {description} = this.state;
     let descrError = (description === '')? 'Введите описание': '';
      this.setState({ descrError });
   };
   validateExecutor = () => {
     const {executor} = this.state;
     let execError = (executor === '' || executor === 'Не выбрано' )? 'Выберите исполнителя': '';
      this.setState({ execError });
   };
   validatePriority = () => {
     const {priority} = this.state;
     let priorError = (priority.length > 0 && priority >= 0 && priority <= 10)? '': 'Задайте приоритет от 0 до 10';
       this.setState({ priorError });
   };
   hintError = (name) => {
     return(name === 'initial'? '': name !== '' ? classes.hintError :classes.hintValid);
   };
   popupHint = (name) => {
     return(name === 'initial'? '': name !== '' ? classes.popupHint :'');
   };


   addTask = (e) => {
     e.preventDefault();
     const {description, executor, priority, descrError, execError, priorError} = this.state;
     const {addTask} = this.props;
     
      if((!descrError && description !== '') && (!execError && executor !== '') && (!priorError && priority !== '')) {
        this.setState({
          task: {
            id: (new Date()).getTime(),
            description,
            executor,
            priority,
          },
        })
        const {task} = this.state;
        addTask(task);

        this.setState({
          task: {
            id: '',
            description: '',
            executor: '',
            priority: '',
          },
          description: '',
          executor: '',
          priority: '',
          descrError: 'initial',
          execError: 'initial',
          priorError: 'initial',
        })
      }
      if(description === '') {
        this.setState({descrError: 'Введите описание' })
      }
      if(executor === '') {
        this.setState({execError: 'Выберите исполнителя' })
      }
      if(priority === '') {
        this.setState({priorError: 'Задайте приоритет от 0 до 10' })
      }
    };

   render(){
     const {users} = this.props;
     const {description, executor, priority, descrError, execError, priorError} = this.state;

     return(
       <div className={classes.bodyForm}>
         <form>
           <textarea
           name='description'
           placeholder='Описание'
           value={description}
           onKeyUp={this.validateDescription}
           onBlur={this.validateDescription}
           onChange={this.handleChange}
           className={`${this.hintError(descrError)} ${classes.textareaStyle} ${classes.textareaDescription}`}
           />
           <select onBlur={this.validateExecutor} name='executor' onChange={this.handleChange} value={executor} className={`${this.hintError(execError)} ${classes.selectStyle}`}>
              {console.log('lol')}
           </select>
           <textarea
           name='priority'
           placeholder='Приоритет'
           onKeyUp={this.validatePriority}
           onBlur={this.validatePriority}
           onChange={this.handleChange}
           value={priority}
           className={`${this.hintError(priorError)} ${classes.textareaStyle} ${classes.textareaPriority}`}
           />
           <InputBtn onClick={this.addTask}/>
         </form>
         <div className={`${this.popupHint(descrError)} ${classes.hint} ${classes.hintDesc}`}>{descrError}</div>
         <div className={`${this.popupHint(execError)} ${classes.hint} ${classes.hintExec}`}>{execError}</div>
         <div className={`${this.popupHint(priorError)} ${classes.hint} ${classes.hintPrior}`}>{priorError}</div>
       </div>
     );
   }
}


export default TodoInput;
