import React, { Component } from 'react';
import { connect } from 'react-redux';

import { removeTask, addTask, increasePriority, decreasePriority} from '../actions/actionCreator';

import TodoInput from '../components/todoInput/todoInput';
import TodoList from '../components/todoList/todoList';
import AppTitle from '../components/appTitle/appTitle';

import classes from './todoBody_style';

class TodoBody extends Component {

  render(){
    const { removeTask, addTask, increasePriority, decreasePriority } = this.props;
    const {user} = this.props.tasks;
    const isTasksExist = user && user.length > 0;
    
    return (
      <div className={classes.todoBody}>
        <AppTitle title='Список задач' />
        <TodoInput addTask={addTask} />
        {isTasksExist && <TodoList
          decreasePriority={decreasePriority}
          increasePriority={increasePriority}
          removeTask={removeTask}
          tasksList={user}
        />}
      </div>
    );
  }
}
const mapStateToProps = state=> {
  console.log(state) // посмотрим, что же у нас в store?
  return {
    tasks: state.tasks,
  }
}

export default connect(mapStateToProps ,
  { removeTask, addTask, increasePriority, decreasePriority }
)(TodoBody);
