import {SET_TASKS, SET_USERS, ADD_TASK} from '../../constants';

export const usersReducer = (state = [], {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return users;
    default:
      return state;
  }
}

export const tasksReducer = (state = [], { type, tasks}) => {
  switch (type) {
    case SET_TASKS :
      return tasks
    default :
      return state;
  }
}
