import {USERS_FETCH, ADD_TASK, REMOVE_TASK, INCREASE_PRIORTY, DECREASE_PRIORITY} from '../../constants';

export const users = (state = [], {type, users}) => {
  switch (type) {
    case USERS_FETCH: 
      return users
    default:
      return state;
  }
}

export const tasks = (state = [], {type, tasks }) => {
  switch (type) {
    case ADD_TASK :
      return tasks
    case REMOVE_TASK :
      return [...state].filter(task => task.id !== id);
    case INCREASE_PRIORTY :
      return [...state].map(task => {
        if(task.id === id) {
          task.priority = ++task.priority;
        }
        return task;
      });
    case DECREASE_PRIORITY :
      return [...state].map(task => {
        if(task.id === id) {
          task.priority = --task.priority;
        }
        return task;
      });
    default :
      return state;
  }
}
