import React,{useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';

import TodoInput from '../components/todoInput/todoInput';
import TodoList from '../components/todoList/todoList';
import AppTitle from '../components/appTitle/appTitle';

import classes from './todoBody_style';

const TodoBody = () => {
  
  const users = useSelector(state => state.usersReducer);
  const tasks = useSelector(state => state.tasksReducer);

  const dispatch = useDispatch();
  
  const loadUsers = () => async (dispatch) => {
    const users = await fetch("/api/users").then(res => res.json());
    dispatch(setUsers(users))
  }

  const loadTasks = () => async (dispatch) => {
    const tasks = await fetch("/api/tasks").then(res => res.json())
    dispatch(setTasks(tasks))
  }

  const onAddTask = async (task) => {
      await fetch("/api/tasks", {
        method: "POST",
        headers: {
          'Accept': 'application/json',
          'Content-type': 'application/json'
        },
        body: JSON.stringify(task)
      })
      alert("Success")
    }

  useEffect(() => {
    dispatch(loadTasks())
    dispatch(loadUsers())
  }, [dispatch])

  const isTasksExist = tasks && tasks.length > 0;
    
  return (
    <div className={classes.todoBody}>
      <AppTitle title='Список задач' />
      <TodoInput users={users} addTask={onAddTask} />
      {isTasksExist && <TodoList
        tasksList={tasks}
      />}
    </div>
  );
}

export default TodoBody;
