import React from 'react';

import PropTypes from 'prop-types';

import classes from './todoBtns_style';


export const TaskListBtn = ({ priority, removeTask, id, increasePriority, decreasePriority }) => {
  if(priority === 10) {
    return(
      <div className={classes.btnItem}>
        <button onClick={() => removeTask(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Удалить</button>
        <button onClick={() => decreasePriority(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Понизить приоритет</button>
      </div>
    );
  } else if (priority === 0) {
    return(
      <div className={classes.btnItem}>
        <button onClick={() => removeTask(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Удалить</button>
        <button onClick={() => increasePriority(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Повысить приоритет</button>
      </div>
    );
  } else {
    return(
      <div className={classes.btnItem}>
        <button onClick={() => removeTask(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Удалить</button>
        <button onClick={() => increasePriority(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Повысить приоритет</button>
        <button onClick={() => decreasePriority(id)} className={`${classes.btn} ${classes.btnTaskList}`} >Понизить приоритет</button>
      </div>
    );
  }
}

export const InputBtn = ({ onClick }) => (
  <button onClick={onClick}  className={`${classes.btn} ${classes.btnAddTask}`}>Добавить задачу</button>
);

TaskListBtn.propTypes = {
  priority: PropTypes.number,
  removeTask: PropTypes.func,
  increasePriority: PropTypes.func,
  decreasePriority: PropTypes.func,
  id: PropTypes.number,
}
TaskListBtn.defaultProps = {
  priority: 0,
  removeTask: () => {},
  increasePriority: () => {},
  decreasePriority: () => {},
  id: 0,
}

InputBtn.propTypes = {
  onClick: PropTypes.func,
}
InputBtn.defaultProps = {
  onClick: () => {},
}
