import React from 'react';
import PropTypes from 'prop-types';

import { TaskListBtn } from '../todoBtns/todoBtns';

import classes from './todoItem_style';

const TodoItem = ({ description, executor, priority, removeTask, id, increasePriority, decreasePriority}) => (
  <div className={classes.bodyList}>
    <div className={classes.bodyItem}>{description}</div>
    <div className={classes.bodyItem}>{executor}</div>
    <div className={classes.bodyItem}>{priority}
      <TaskListBtn
        priority={priority}
        increasePriority={increasePriority}
        decreasePriority={decreasePriority}
        removeTask={removeTask}
        id={id}
      />
    </div>
  </div>
);


TodoItem.propTypes = {
  description: PropTypes.string,
  executor: PropTypes.string,
  priority: PropTypes.number,
  removeTask: PropTypes.func,
  increasePriority: PropTypes.func,
  decreasePriority: PropTypes.func,
  id: PropTypes.number,
}
TodoItem.defaultProps = {
  description: '',
  executor: '',
  priority: 0,
  removeTask: () => {},
  increasePriority: () => {},
  decreasePriority: () => {},
  id: 0,
}

export default TodoItem;
