import {SET_TASKS, SET_USERS, ADD_TASK} from '../../constants';
import {setUsers} from '../actions/actionCreator';

const initialStateUsers = [
	{id: 0, name: 'Не выбрано'},
	{id: 1, name: 'alex'},
	{id: 2, name: 'frenk'},
	{id: 3, name: 'fred'},
];

export const usersReducer = (state = initialStateUsers, {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return {...state, users}
    default:
      return state;
  }
}

export const loadUsers = () =>  (dispatch) => {
  const users =  fetch("/api/users").then(res => res.json())
  dispatch(setUsers(users))
} 

export const tasksReducer = (state = [], { type, tasks, id, description, executor, priority }) => {
  switch (type) {
    case SET_TASKS :
      return {...state, tasks}
    case ADD_TASK :
      return {
          id,
          description,
          executor,
          priority: +priority,
        };
    default :
      return state;
  }
}
