import { ADD_TASK, REMOVE_TASK, INCREASE_PRIORTY, DECREASE_PRIORITY } from '../../constants';

export const addTask = (id, description, executor, priority) => ({
  type: ADD_TASK,
  id,
  description,
  executor,
  priority
});

export const removeTask = id => ({
  type: REMOVE_TASK,
  id
})

export const increasePriority = id => ({
  type: INCREASE_PRIORTY,
  id
})

export const decreasePriority = id => ({
  type: DECREASE_PRIORITY,
  id
})

export const tasksFetchData = url => {
  return (dispatch) => {
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error(response.statusText)
        }
        return response;
      })
      .then(response => response.json())
      .then()
  }
}
