import React,{useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {loadUsers, loadTasks} from '../reducers/reducers';

import TodoInput from '../components/todoInput/todoInput';
import TodoList from '../components/todoList/todoList';
import AppTitle from '../components/appTitle/appTitle';

import classes from './todoBody_style';

const TodoBody = () => {
  
  const users = useSelector(state => state.usersReducer);
  const tasks = useSelector(state => state.tasksReducer);

  const dispatch = useDispatch();
  
  const onAddTask = async (task) => {
    await fetch("/api/tasks", {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify(task)
  })
  }

  useEffect(() => {
    dispatch(loadTasks())
    dispatch(loadUsers())
  }, [dispatch])
    
  return (
    <div className={classes.todoBody}>
      <AppTitle title='Список задач' />
      <TodoInput users={users} addTask={onAddTask} />
      <TodoList tasksList={tasks} />
    </div>
  );
}

export default TodoBody;
