import {USERS_LOADED, USERS_REQUESTED, USERS_ERROR, TASKS_LOADED, } from '../../constants';
import {usersLoaded, tasksLoaded, usersError} from '../actions/actionCreator';

const initialState = {
  users: [],
  loaded: true,
  error: null
}

export const usersReducer = (state = initialState, {type, users}) => {
  switch (type) {
    case USERS_LOADED:
      return {
        users
      }
    default:
      return state;
  }
}

export const tasksReducer = (state = [], { type, tasks }) => {
  switch (type) {
    case TASKS_LOADED:
      return tasks
    default:
      return state;
  }
}

export const loadUsers = () => async (dispatch) => {
  await fetch("/api/users")
  .then(res => res.json())
  .then(users => dispatch(usersLoaded(users)))
  .catch(err => dispatch(usersError(err)))
} 

export const loadTasks = () => async (dispatch) => {
  const tasks = await fetch("/api/tasks").then(res => res.json())
  dispatch(tasksLoaded(tasks))
}

export const saveTask = (task) => async (dispatch) => {
  await fetch("/api/tasks", {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify(task)
  })
  .then(res => res.json())
  .then(tasks => dispatch(tasksLoaded(tasks)))
}

export const changePriority = (id, nameAction) => async (dispatch) => {
  await fetch(`/api/tasks/${id}`, {
    method: "PUT",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify({nameAction})
  })
  .then(res => res.json())
  .then(tasks => dispatch(tasksLoaded(tasks)))
}

export const removeTask = (id) => async (dispatch) => {
  await fetch(`/api/tasks/${id}`, {
    method: "DELETE",
  })
  .then(res => res.json())
  .then(tasks => dispatch(tasksLoaded(tasks)))
}
