import {USERS_LOADED, USERS_REQUESTED, USERS_ERROR, TASKS_LOADED, } from '../../constants';
import {setUsers, setTasks, errUsers} from '../actions/actionCreator';

export const usersReducer = (state = {}, {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return users;
    default:
      return state;
  }
}

export const tasksReducer = (state = [], { type, tasks }) => {
  switch (type) {
    case SET_TASKS:
      return tasks
    default:
      return state;
  }
}

export const loadUsers = () => async (dispatch) => {
  await fetch("/api/users")
  .then(res => res.json())
  .then(users => dispatch(setUsers(users)))
  .catch(err => dispatch(errUsers(err)));
} 

export const loadTasks = () => async (dispatch) => {
  const tasks = await fetch("/api/tasks").then(res => res.json())
  dispatch(setTasks(tasks))
}

export const saveTask = (task) => async (dispatch) => {
  await fetch("/api/tasks", {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify(task)
  })
  .then(res => res.json())
  .then(res => dispatch(setTasks(res)))
}

export const changePriority = (id, nameAction) => async (dispatch) => {
  await fetch(`/api/tasks/${id}`, {
    method: "PUT",
    headers: {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    },
    body: JSON.stringify({nameAction})
  })
  .then(res => res.json())
  .then(res => dispatch(setTasks(res)))
}

export const removeTask = (id) => async (dispatch) => {
  await fetch(`/api/tasks/${id}`, {
    method: "DELETE",
  })
  .then(res => res.json())
  .then(res => dispatch(setTasks(res)))
}
