import React, { Fragment } from 'react';
import PropTypes from 'prop-types';

import TodoItem from '../todoItem/todoItem';




const TodoList = ({ tasksList, removeTask, increasePriority, decreasePriority }) => (
  <Fragment>
    {tasksList.sort((a, b) => {return b.priority - a.priority}).map(({ id, description, executor, priority }) => (
        <TodoItem
          increasePriority={increasePriority}
          decreasePriority={decreasePriority}
          removeTask={removeTask}
          id={id}
          key={id}
          description={description}
          executor={executor}
          priority={priority}
        />
    ))}
  </Fragment>
);

TodoList.propTypes = {
  tasksList: PropTypes.array,
  removeTask: PropTypes.func,
  increasePriority: PropTypes.func,
  decreasePriority: PropTypes.func,
}
TodoList.defaultProps = {
  tasksList: [],
  removeTask: () => {},
  increasePriority: () => {},
  decreasePriority: () => {},
}

export default TodoList;
