import { ADD_TASK, REMOVE_TASK, INCREASE_PRIORTY, DECREASE_PRIORITY} from '../../constants';
import {load} from 'redux-localstorage-simple';

let TASKS = load({ namespace: 'todo-list' });

if (!TASKS || !TASKS.tasks|| !TASKS.tasks.length) {
  TASKS = {
    tasks: [],
  }
}

const tasks = (state= TASKS.tasks, { id, description, executor, priority, type }) => {
  switch (type) {
    case ADD_TASK :
      return [
        ...state, {
          id,
          description,
          executor,
          priority: +priority,
        }
      ];
    case REMOVE_TASK :
      return [...state].filter(task => task.id !== id);
    case INCREASE_PRIORTY :
      return [...state].map(task => {
        if(task.id === id) {
          task.priority = ++task.priority;
        }
        return task;
      });
    case DECREASE_PRIORITY :
      return [...state].map(task => {
        if(task.id === id) {
          task.priority = --task.priority;
        }
        return task;
      });
    default:
      return state;
  }
}

export default tasks;
