import {SET_TASKS, SET_USERS} from '../../constants';

export const usersReducer = (state = [], {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return users;
    default:
      return state;
  }
}

export const tasksReducer = (state = [], { type, task ,tasks}) => {
  switch (type) {
    case SET_TASKS:
      return tasks
    case 'ADD_TASK':
      return [
        ...state,  {task}
      ];
    default:
      return state;
  }
}
