import {GET_TASKS, GET_USERS, ADD_TASK, REMOVE_TASK, INCREASE_PRIORTY, DECREASE_PRIORITY} from '../../constants';

export const users = (state = [], {type, users}) => {
  switch (type) {
    case GET_USERS: 
      return users
    default:
      return state;
  }
}

export const tasks = (state = [], { type, tasks, id, description, executor, priority }) => {
  switch (type) {
    case GET_TASKS :
      return {...state, tasks}
    case ADD_TASK :
      return {
          id,
          description,
          executor,
          priority: +priority,
        };
    case REMOVE_TASK :
      return [...state].filter(task => task.id !== id);
    case INCREASE_PRIORTY :
      return [...state].map(task => {
        if(task.id === id) {
          task.priority = ++task.priority;
        }
        return task;
      });
    case DECREASE_PRIORITY :
      return [...state].map(task => {
        if(task.id === id) {
          task.priority = --task.priority;
        }
        return task;
      });
    default :
      return state;
  }
}
