import {SET_TASKS, SET_USERS, ADD_TASK} from '../../constants';

export const users = (state = [], {type, users}) => {
  switch (type) {
    case GET_USERS: 
      return users
    default:
      return state;
  }
}

export const tasksReducer = (state = [], { type, tasks, id, description, executor, priority }) => {
  switch (type) {
    case GET_TASKS :
      return {...state, tasks}
    case ADD_TASK :
      return {
          id,
          description,
          executor,
          priority: +priority,
        };
    default :
      return state;
  }
}
