import {SET_TASKS, SET_USERS, ADD_TASK} from '../../constants';
import {setUsers, setTasks} from '../actions/actionCreator';

const initialStateUsers = [];

export const usersReducer = (state = initialStateUsers, {type, users}) => {
  switch (type) {
    case SET_USERS: 
      return {...state, users}
    default:
      return state;
  }
}

export const loadUsers = () => async (dispatch) => {
  const users = await fetch("/api/users").then(res => res.json())
  dispatch(setUsers(users))
} 

export const tasksReducer = (state = [], { type, tasks, id, description, executor, priority }) => {
  switch (type) {
    case SET_TASKS :
      return {...state, tasks}
    case ADD_TASK :
      return {
          id,
          description,
          executor,
          priority: +priority,
        };
    default :
      return state;
  }
}

export const loadTasks = () => async (dispatch) => {
  const tasks = await fetch("/api/users").then(res => res.json())
  dispatch(setTasks(tasks))
}
