import {GET_TASKS, GET_USERS, ADD_TASK, REMOVE_TASK, INCREASE_PRIORTY, DECREASE_PRIORITY } from '../../constants';

export const addTak = (id, description, executor, priority) => ({
  type: ADD_TASK,
  id,
  description,
  executor,
  priority
});

export const removeTask = id => ({
  type: REMOVE_TASK,
  id
})

export const increasePriority = id => ({
  type: INCREASE_PRIORTY,
  id
})

export const decreasePriority = id => ({
  type: DECREASE_PRIORITY,
  id
})

export const tasksPostSuccess = (tasks) => ({
  type: 'POST_TASKS',
  tasks
})

export const tasksPostData = (url= '/api/tasks', addTask = {}) => {
  return (dispatch) => {
    fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(addTask)
    })
      .then(response => response.json())
      .then(addTask => dispatch(tasksPostSuccess(addTask)))
  }
}

export const tasksFetchSuccess = (tasks) => ({
  type: GET_TASKS,
  tasks
})

export const tasksFetchData = url => {
  return (dispatch) => {
    fetch(url)
      .then(response => response.json())
      .then(tasks => dispatch(tasksFetchSuccess(tasks)))
  }
}

export const usersFetchSuccess = (users) => ({
  type: GET_USERS,
  users
})

export const usersFetchData = url => {
  return (dispatch) => {
    fetch(url)
      .then(response => response.json())
      .then(users => dispatch(usersFetchSuccess(users)))
  }
}
