import React,{useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';

import {addTask} from '../actions/actionCreator';
import { loadUsers, loadTasks } from '../reducers/reducers';

import TodoInput from '../components/todoInput/todoInput';
import TodoList from '../components/todoList/todoList';
import AppTitle from '../components/appTitle/appTitle';

import classes from './todoBody_style';

const TodoBody = () => {
  
  const users = useSelector(state => state.users);
  const tasks = useSelector(state => state.tasks);
  const dispatch = useDispatch();
  
  const onAddTask = (task) => {
    dispatch(addTask(task))
  }

  useEffect(() => {
    dispatch(loadTasks())
    dispatch(loadUsers())
  }, [])

  const isTasksExist = tasks && tasks.length > 0;
    
  return (
    <div className={classes.todoBody}>
      <AppTitle title='Список задач' />
      <TodoInput users={users} addTask={onAddTask} />
      {isTasksExist && <TodoList
        tasksList={tasks}
      />}
    </div>
  );
}

export default TodoBody;
