import React, { Component } from 'react';
import { connect } from 'react-redux';

import {addTask, tasksFetchData, usersFetchData, removeTask, increasePriority, decreasePriority} from '../actions/actionCreator';

import TodoInput from '../components/todoInput/todoInput';
import TodoList from '../components/todoList/todoList';
import AppTitle from '../components/appTitle/appTitle';

import classes from './todoBody_style';

class TodoBody extends Component {

  componentDidMount(){
    this.props.fetchDataUsers('/api/users');
    this.props.fetchDataTasks('/api/tasks');
    
  }

  render(){
    const { users, tasks, addTask ,removeTaskAction , increasePriorityAction, decreasePriorityAction } = this.props;
    const isTasksExist = tasks && tasks.length > 0;
    
    return (
      <div className={classes.todoBody}>
        <AppTitle title='Список задач' />
        <TodoInput users={users} addTask={addTask} />
        {isTasksExist && <TodoList
          decreasePriority={decreasePriorityAction}
          increasePriority={increasePriorityAction}
          removeTask={removeTaskAction}
          tasksList={tasks}
        />}
      </div>
      
    );
  }
}
const mapStateToProps = store => ({
    users: store.users,
    tasks: store.tasks,
})

const mapDispatchToProps = dispatch => ({
    fetchDataUsers: url => {dispatch(usersFetchData(url))},
    fetchDataTasks: url => {dispatch(tasksFetchData(url))},
    addTask: task => {dispatch(addTask(task))},
    removeTaskAction: id => dispatch(removeTask(id)),
    increasePriorityAction: id => dispatch(increasePriority(id)),
    decreasePriorityAction: id => dispatch(decreasePriority(id)),
})

export default connect(mapStateToProps , mapDispatchToProps
)(TodoBody);
