import {USERS_LOADED, USERS_REQUESTED,USERS_ERROR, TASKS_LOADED, } from '../../constants';

export const usersLoaded = (users) => ({
  type: USERS_LOADED,
  users
});

export const usersRequested = (err) => ({
  type: USERS_REQUESTED
});

export const usersError = (err) => ({
  type: USERS_ERROR
});


export const tasksLoaded = (tasks) => ({
  type: TASKS_LOADED,
  tasks
});
