import {GET_TASKS, GET_USERS, ADD_TASK} from '../../constants';

export const addTask = (task) => ({
  type: ADD_TASK,
  task
});

export const loadTasks = (tasks) => ({
  type: GET_TASKS,
  tasks
})

export const usersFetchSuccess = (users) => ({
  type: GET_USERS,
  users
})
